function myUpload($test,$testTemp,$loc,$qid){
   //echo "Still Here";
	$filemain = ".$loc/$qid.$test";
	echo $filemain.'<br>';
	//echo $test.'<br>';
	//echo $testTemp.'<br>';	
	if (move_uploaded_file($testTemp, $filemain)) {
	   echo "File is valid, and was successfully uploaded.\n";
	   $filemain = "$loc/$qid.$test";
	   return $filemain;
	} else {
	   echo "Possible file upload attack!\n";
	   return 0;
	}
}
